import React, { useEffect, useState } from "react";
import { useSearchParams } from "react-router-dom";
import axios from "axios";
import "../styles/ComparisonPage.css";

const ComparisonPage = () => {
    const [searchParams] = useSearchParams();
    const customerId = searchParams.get("customerId");
    const [comparisonResult, setComparisonResult] = useState(null);
    const [error, setError] = useState("");

    useEffect(() => {
        const fetchComparisonResult = async () => {
            if (!customerId) {
                setError("Customer ID is missing.");
                return;
            }

            try {
                const response = await axios.post(
                    `http://localhost:8000/compare-images/`,
                    {
                        base_id: 1, // Replace with your actual base_id
                        customer_id: parseInt(customerId),
                    }
                );
                setComparisonResult(response.data);
            } catch (err) {
                console.error("Error fetching comparison result:", err);
                setError("Failed to fetch comparison results.");
            }
        };

        fetchComparisonResult();
    }, [customerId]);

    return (
        <div className="comparison-page">
            <h1>Comparison Result</h1>
            {error && <p className="error">{error}</p>}
            {comparisonResult ? (
                <div className="results-container">
                    <p><strong>Front Similarity:</strong> {comparisonResult.front_similarity}</p>
                    <p><strong>Back Similarity:</strong> {comparisonResult.back_similarity}</p>
                    <p><strong>Average SSI:</strong> {comparisonResult.average_ssi}</p>
                    <p><strong>Overall Condition:</strong> {comparisonResult.overall_condition}</p>
                </div>
            ) : (
                !error && <p>Loading results...</p>
            )}
        </div>
    );
};

export default ComparisonPage;
