import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/InspectionPage.css";
import GoBackButton from "../components/GoBackButton"; // Adjust the import path

const InspectionPage = () => {
  const navigate = useNavigate();
  const [selections, setSelections] = useState({
    packagingTeared: false,
    packagingWearTear: false,
    bioStain: false,
    nonBioStain: false,
    surfaceTears: false,
  });

  const handleSelectionChange = (event) => {
    const { name, checked } = event.target;
    setSelections((prev) => ({
      ...prev,
      [name]: checked,
    }));
  };

  const handleProceed = () => {
    console.log("User Selections:", selections);
    navigate("/upload-images");
  };

  return (
    <div className="inspection-page">
      <GoBackButton />
      <h1>Product Inspection</h1>
      <p className="instruction">
        Review the following conditions and select the appropriate options.
      </p>
      <div className="cards-container">
        {/* Left Card */}
        <div className="card">
          <h3 className="card-title">Product Condition</h3>
          <div className="card-content">
            <label className="checkbox-label">
              <input
                type="checkbox"
                name="packagingTeared"
                checked={selections.packagingTeared}
                onChange={handleSelectionChange}
              />
              Is the packaging intact with minimal wear and tear? — Mark as "New"
            </label>
            <label className="checkbox-label">
              <input
                type="checkbox"
                name="packagingWearTear"
                checked={selections.packagingWearTear}
                onChange={handleSelectionChange}
              />
              Is the packaging torn or resealed? — Mark as "Not New"
            </label>
          </div>
        </div>

        {/* Right Card */}
        <div className="card">
          <h3 className="card-title">Stain & Surface Issues</h3>
          <div className="card-content">
            <label className="checkbox-label">
              <input
                type="checkbox"
                name="bioStain"
                checked={selections.bioStain}
                onChange={handleSelectionChange}
              />
              Bio Stains
            </label>
            <label className="checkbox-label">
              <input
                type="checkbox"
                name="nonBioStain"
                checked={selections.nonBioStain}
                onChange={handleSelectionChange}
              />
              Non-Bio Stains
            </label>
            <label className="checkbox-label">
              <input
                type="checkbox"
                name="surfaceTears"
                checked={selections.surfaceTears}
                onChange={handleSelectionChange}
              />
              Tears on the surface
            </label>
          </div>
        </div>
      </div>
      <button
        type="button"
        className="proceed-button"
        onClick={handleProceed}
        disabled={!Object.values(selections).some((value) => value === true)}
      >
        Proceed →
      </button>
    </div>
  );
};

export default InspectionPage;
