import React, { useState, useEffect } from "react";
import BrandDropdown from "../components/BrandDropdown";
import ItemDropdown from "../components/ItemDropdown";
import apiClient from "../api/apiClient";

const Home = () => {
  const [brands, setBrands] = useState([]);
  const [items, setItems] = useState([]);
  const [selectedBrand, setSelectedBrand] = useState("");

  // Fetch brands when component loads
  useEffect(() => {
    apiClient.get("/brand")
      .then((response) => {
        setBrands(response.data); // Assuming response is an array of brands
      })
      .catch((error) => {
        console.error("Error fetching brands:", error);
      });
  }, []);

  // Fetch items based on selected brand
  const handleBrandChange = (event) => {
    const brandId = event.target.value;
    setSelectedBrand(brandId);

    if (brandId) {
      apiClient.get(`/items/${brandId}`)
        .then((response) => {
          setItems(response.data); // Assuming response is an array of items
        })
        .catch((error) => {
          console.error("Error fetching items:", error);
        });
    } else {
      setItems([]);
    }
  };

  return (
    <div>
      <h1>Brand and Item Selector</h1>
      <BrandDropdown brands={brands} onChange={handleBrandChange} />
      <ItemDropdown items={items} disabled={!selectedBrand} />
    </div>
  );
};

export default Home;
