import React, { useState } from "react";
import axios from "axios";
import "../styles/ItemDataUploadPage.css";

const ItemDataUploadPage = () => {
    const [csvFile, setCsvFile] = useState(null);
    const [searchQuery, setSearchQuery] = useState("");
    const [searchResults, setSearchResults] = useState([]);
    const [notification, setNotification] = useState("");

    const handleFileUpload = async (e) => {
        e.preventDefault();

        if (!csvFile) {
            setNotification("Please select a CSV file to upload.");
            return;
        }

        const formData = new FormData();
        formData.append("file", csvFile);

        try {
            const response = await axios.post(
                "http://localhost:8000/upload-items-csv",
                formData,
                { headers: { "Content-Type": "multipart/form-data" } }
            );
            setNotification(response.data.message);
        } catch (error) {
            console.error("Error uploading CSV:", error);
            setNotification("An error occurred while uploading the CSV.");
        }
    };

    const handleSearch = async () => {
        try {
            const response = await axios.get("http://localhost:8000/search-items", {
                params: { query: searchQuery },
            });
            setSearchResults(response.data);
        } catch (error) {
            console.error("Error searching items:", error);
            setNotification("An error occurred while searching for items.");
        }
    };

    return (
        <div className="item-admin-page">
            <h1>Item Admin Page</h1>

            <div className="upload-section">
                <h2>Upload Items via CSV</h2>
                <form onSubmit={handleFileUpload}>
                    <input
                        type="file"
                        accept=".csv"
                        onChange={(e) => setCsvFile(e.target.files[0])}
                    />
                    <button type="submit">Upload</button>
                </form>
            </div>

            <div className="search-section">
                <h2>Search Items</h2>
                <input
                    type="text"
                    placeholder="Search by item_number, description, or brand_id"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                />
                <button onClick={handleSearch}>Search</button>
            </div>

            {notification && <p className="notification">{notification}</p>}

            <div className="results-section">
                {searchResults.length > 0 ? (
                    <table>
                        <thead>
                            <tr>
                                <th>Item Number</th>
                                <th>Description</th>
                                <th>Brand ID</th>
                                <th>Category</th>
                                <th>Configuration</th>
                            </tr>
                        </thead>
                        <tbody>
                            {searchResults.map((item, index) => (
                                <tr key={index}>
                                    <td>{item.item_number}</td>
                                    <td>{item.item_description}</td>
                                    <td>{item.brand_id}</td>
                                    <td>{item.category}</td>
                                    <td>{item.configuration}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                ) : (
                    <p>No items found.</p>
                )}
            </div>
        </div>
    );
};

export default ItemDataUploadPage;
