import React, { useState } from "react";
import axios from "axios"; // Import Axios for API requests
import "../styles/ManualScanPage.css"; // Add styles if needed

const ManualScanPage = () => {
  const [inputType, setInputType] = useState(""); // Serial or Return Order
  const [inputValue, setInputValue] = useState("");
  const [fields, setFields] = useState(null); // Dynamic fields from API
  const [error, setError] = useState("");

  const handleFetchFields = async () => {
    try {
      const endpoint =
        inputType === "serial"
          ? `/api/serial-number/${inputValue}`
          : `/api/return-order/${inputValue}`;

      const response = await axios.get(endpoint); // Adjust URL based on your backend
      setFields(response.data); // Assuming response contains fields
      setError("");
    } catch (err) {
      setError("Failed to fetch data. Please check your input.");
      setFields(null);
    }
  };

  return (
    <div className="manual-scan-page">
      <h1>Manual Scan</h1>
      <div className="input-container">
        <label>
          <input
            type="radio"
            name="inputType"
            value="serial"
            onChange={() => setInputType("serial")}
          />
          Serial Number
        </label>
        <label>
          <input
            type="radio"
            name="inputType"
            value="returnOrder"
            onChange={() => setInputType("returnOrder")}
          />
          Return Order Number
        </label>
        <input
          type="text"
          placeholder={`Enter ${
            inputType === "serial" ? "Serial Number" : "Return Order Number"
          }`}
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          disabled={!inputType}
        />
        <button onClick={handleFetchFields} disabled={!inputValue}>
          Fetch Fields
        </button>
      </div>
      {error && <p className="error">{error}</p>}
      {fields && (
        <div className="fields-container">
          <h2>Fetched Fields</h2>
          {Object.entries(fields).map(([key, value]) => (
            <div key={key}>
              <label>{key}:</label>
              <input type="text" value={value} readOnly />
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ManualScanPage;
