import React, { useState } from "react";
import axios from "axios";
import "../styles/UploadImagesPage.css";

const UploadImagesPage = () => {
    const [id, setId] = useState("");
    const [frontImage, setFrontImage] = useState(null);
    const [backImage, setBackImage] = useState(null);
    const [uploadStatus, setUploadStatus] = useState("");
    const [isUploadSuccessful, setIsUploadSuccessful] = useState(false);
    const [showPopup, setShowPopup] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!id || !frontImage || !backImage) {
            setUploadStatus("Please provide an ID and both images.");
            setIsUploadSuccessful(false);
            setShowPopup(true);
            return;
        }

        const formData = new FormData();
        formData.append("front_image", frontImage);
        formData.append("back_image", backImage);

        try {
            const response = await axios.post(
                `http://localhost:8000/upload-customer-images?id=${id}`,
                formData,
                {
                    headers: { "Content-Type": "multipart/form-data" },
                }
            );
            setUploadStatus(response.data.message || "Images uploaded successfully!");
            setIsUploadSuccessful(true);
        } catch (error) {
            console.error("Error uploading images:", error.response?.data || error);
            setUploadStatus("An error occurred while uploading images.");
            setIsUploadSuccessful(false);
        }
        setShowPopup(true);
    };

    const handleProceed = () => {
        alert("Proceeding to the next step!");
    };

    const handleClosePopup = () => {
        setShowPopup(false);
    };

    const handleBack = () => {
        window.history.back(); // Navigate back to the previous page
    };

    return (
        <div className="upload-images-page">
            <h1>Upload Images</h1>
            <form onSubmit={handleSubmit} className="upload-form">
                <div className="form-group">
                    <label htmlFor="id">Customer ID:</label>
                    <input
                        type="text"
                        id="id"
                        value={id}
                        onChange={(e) => setId(e.target.value)}
                        placeholder="Enter Customer ID"
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="front-image">Front Image:</label>
                    <input
                        type="file"
                        id="front-image"
                        accept="image/*"
                        onChange={(e) => setFrontImage(e.target.files[0])}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="back-image">Back Image:</label>
                    <input
                        type="file"
                        id="back-image"
                        accept="image/*"
                        onChange={(e) => setBackImage(e.target.files[0])}
                        required
                    />
                </div>
                <button type="submit" className="button upload-button">
                    Upload Images
                </button>
            </form>
            <div className="button-container">
                <button className="button back-button" onClick={handleBack}>
                    Back
                </button>
                <button
                    className="button proceed-button"
                    onClick={handleProceed}
                    disabled={!isUploadSuccessful}
                >
                    Proceed
                </button>
            </div>

            {showPopup && (
                <div className="popup">
                    <div className="popup-content">
                        <p>{uploadStatus}</p>
                        <button className="button close-button" onClick={handleClosePopup}>
                            Close
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default UploadImagesPage;
