import React, { useEffect, useState } from "react";
import { useSearchParams } from "react-router-dom";
import axios from "axios";
import "../styles/ComparisonPage.css";

const ComparisonPage = () => {
    const [searchParams] = useSearchParams();
    const customerId = searchParams.get("customerId");
    const [comparisonResult, setComparisonResult] = useState(null);
    const [error, setError] = useState("");

    useEffect(() => {
        const fetchComparisonResult = async () => {
            if (!customerId) {
                setError("Customer ID is missing.");
                return;
            }

            try {
                const response = await axios.post(
                    `http://localhost:8000/compare-images/`,
                    {
                        base_id: 1, // Replace with your actual base_id
                        customer_id: parseInt(customerId),
                    },
                    {
                        headers: {
                            "Content-Type": "application/json",
                        },
                    }
                );
                setComparisonResult(response.data);
            } catch (err) {
                console.error("Error fetching comparison result:", err);
                setError(err.response?.data?.detail || "Failed to fetch comparison results.");
            }
        };

        fetchComparisonResult();
    }, [customerId]);

    return (
        <div className="comparison-page">
            <h1>Comparison Result</h1>
            {error && <p className="error">{error}</p>}
            {comparisonResult ? (
                <div className="results-grid">
                    <div className="result-card">
                        <h2>Front Image</h2>
                        <p><strong>Cosine Similarity:</strong> {comparisonResult.front_similarity.toFixed(2)}</p>
                        <p><strong>SSI:</strong> {comparisonResult.ssi_front.toFixed(2)}</p>
                    </div>
                    <div className="result-card">
                        <h2>Back Image</h2>
                        <p><strong>Cosine Similarity:</strong> {comparisonResult.back_similarity.toFixed(2)}</p>
                        <p><strong>SSI:</strong> {comparisonResult.ssi_back.toFixed(2)}</p>
                    </div>
                    <div className="result-summary">
                        <h2>Summary</h2>
                        <p><strong>Average SSI:</strong> {comparisonResult.average_ssi.toFixed(2)}</p>
                        <p><strong>Overall Condition:</strong> {comparisonResult.overall_condition}</p>
                    </div>
                </div>
            ) : (
                !error && <p>Loading results...</p>
            )}
        </div>
    );
};

export default ComparisonPage;
