import React, { useState, useContext } from "react";
import { InspectionContext } from "../context/InspectionContext";
import axios from "axios";

const UploadImagesPage = () => {
  const { inspectionData, customerItemId } = useContext(InspectionContext); // Get data from context
  const [frontImage, setFrontImage] = useState(null);
  const [backImage, setBackImage] = useState(null);
  const [notification, setNotification] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!frontImage || !backImage || !customerItemId) {
      setNotification("Please upload both images and ensure customer ID is available.");
      return;
    }

    const formData = new FormData();
    formData.append("front_image", frontImage);
    formData.append("back_image", backImage);
    formData.append("customer_item_data_id", customerItemId);

    // Append inspection data
    Object.keys(inspectionData).forEach((key) => {
      formData.append(key, inspectionData[key]);
    });

    try {
      const response = await axios.post("http://localhost:8000/upload-customer-images", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      if (response.status === 200) {
        setNotification("Images uploaded successfully!");
      } else {
        setNotification("Failed to upload images.");
      }
    } catch (error) {
      console.error("Error uploading images:", error);
      setNotification("An error occurred.");
    }
  };

  return (
    <div>
      <h1>Upload Images</h1>
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={(e) => setFrontImage(e.target.files[0])} />
        <input type="file" onChange={(e) => setBackImage(e.target.files[0])} />
        <button type="submit">Upload</button>
      </form>
      {notification && <p>{notification}</p>}
    </div>
  );
};

export default UploadImagesPage;
