import React, { useState } from "react";
import axios from "axios";
import "../styles/ManualScanPage.css";

const ManualScanPage = () => {
  const [orderNumber, setOrderNumber] = useState("");
  const [data, setData] = useState(null);
  const [error, setError] = useState("");

  const fetchItemDetails = async () => {
    try {
      const response = await axios.get(
        `http://localhost:8000/item_order_instance`, // Adjust to your API URL
        {
          params: { order_number: orderNumber },
        }
      );
      setData(response.data);
      setError("");
    } catch (err) {
      setError(err.response?.data?.detail || "Failed to fetch details.");
      setData(null);
    }
  };

  return (
    <div className="manual-scan-page">
      <h1>Manual Scan</h1>
      <div className="input-container">
        <input
          type="text"
          placeholder="Enter Serial or Return Order Number"
          value={orderNumber}
          onChange={(e) => setOrderNumber(e.target.value)}
        />
        <button onClick={fetchItemDetails} disabled={!orderNumber}>
          Fetch Details
        </button>
      </div>
      {error && <p className="error">{error}</p>}
      {data && (
        <div className="details-container">
          <h2>Item Details</h2>
          <pre>{JSON.stringify(data, null, 2)}</pre>
        </div>
      )}
    </div>
  );
};

export default ManualScanPage;
