import React, { useState } from "react";
import axios from "axios";
import "../styles/ComparisonPage.css";

const ComparisonPage = () => {
  const [baseId, setBaseId] = useState("");
  const [customerId, setCustomerId] = useState("");
  const [comparisonResults, setComparisonResults] = useState(null);
  const [error, setError] = useState("");

  const handleCompare = async () => {
    try {
      setError("");
      setComparisonResults(null);
      const response = await axios.post("http://localhost:8000/compare-images/", {
        base_id: parseInt(baseId),
        customer_id: parseInt(customerId),
      });

      setComparisonResults(response.data);
    } catch (err) {
      console.error("Error during comparison:", err);
      setError(
        err.response?.data?.detail || "An unexpected error occurred. Please try again."
      );
    }
  };

  return (
    <div className="comparison-page">
      <h1>Image Comparison</h1>
      <p className="instruction">Enter Base and Customer IDs to compare images.</p>
      <div className="input-container">
        <input
          type="number"
          placeholder="Base ID"
          value={baseId}
          onChange={(e) => setBaseId(e.target.value)}
        />
        <input
          type="number"
          placeholder="Customer ID"
          value={customerId}
          onChange={(e) => setCustomerId(e.target.value)}
        />
        <button onClick={handleCompare} disabled={!baseId || !customerId}>
          Compare
        </button>
      </div>
      {error && <div className="error-box">{error}</div>}
      {comparisonResults && (
        <div className="results-container">
          <h2>Comparison Results</h2>
          <p><strong>Front Similarity:</strong> {comparisonResults.front_similarity.toFixed(2)}</p>
          <p><strong>Back Similarity:</strong> {comparisonResults.back_similarity.toFixed(2)}</p>
          <p><strong>Front SSI:</strong> {comparisonResults.ssi_front.toFixed(2)}</p>
          <p><strong>Back SSI:</strong> {comparisonResults.ssi_back.toFixed(2)}</p>
          <p><strong>Average SSI:</strong> {comparisonResults.average_ssi.toFixed(2)}</p>
          <p><strong>Overall Condition:</strong> {comparisonResults.overall_condition}</p>
        </div>
      )}
    </div>
  );
};

export default ComparisonPage;
