import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/JudgmentPage.css";

const JudgmentPage = () => {
  const navigate = useNavigate();
  const [selections, setSelections] = useState({
    packagingTeared: false,
    packagingWearTear: false,
    bioStain: false,
    nonBioStain: false,
    surfaceTears: false,
  });

  const handleSelectionChange = (event) => {
    const { name, checked } = event.target;
    setSelections((prev) => ({
      ...prev,
      [name]: checked,
    }));
  };

  const handleProceed = () => {
    console.log("User Selections:", selections);
    navigate("/upload-images");
  };

  return (
    <div className="judgment-page">
      <h1>Product Judgment Checklist</h1>
      <p className="instruction">
        Please review the following conditions and select the appropriate options.
      </p>
      <form className="judgment-form">
        <div className="checkbox-group">
          <h3>User Needs to Make Judgments:</h3>
          <label className="checkbox-label">
            <input
              type="checkbox"
              name="packagingTeared"
              checked={selections.packagingTeared}
              onChange={handleSelectionChange}
            />
            Product packaging is teared and retaped – Cannot be considered as new
          </label>
          <label className="checkbox-label">
            <input
              type="checkbox"
              name="packagingWearTear"
              checked={selections.packagingWearTear}
              onChange={handleSelectionChange}
            />
            Product is in company package with normal wear and tear – Considered as new
          </label>
        </div>

        <div className="checkbox-group">
          <h3>Visible Stains:</h3>
          <label className="checkbox-label">
            <input
              type="checkbox"
              name="bioStain"
              checked={selections.bioStain}
              onChange={handleSelectionChange}
            />
            Bio Stain
          </label>
          <label className="checkbox-label">
            <input
              type="checkbox"
              name="nonBioStain"
              checked={selections.nonBioStain}
              onChange={handleSelectionChange}
            />
            Non-Bio Stain
          </label>
          <label className="checkbox-label">
            <input
              type="checkbox"
              name="surfaceTears"
              checked={selections.surfaceTears}
              onChange={handleSelectionChange}
            />
            Tears on the mattress surface
          </label>
        </div>

        <button
          type="button"
          className="proceed-button"
          onClick={handleProceed}
          disabled={
            !Object.values(selections).some((value) => value === true)
          }
        >
          Proceed →
        </button>
      </form>
    </div>
  );
};

export default JudgmentPage;
