import React, { useState } from "react";
import axios from "axios";
import "../styles/UploadImagesPage.css";

const UploadImagesPage = () => {
  const [frontImage, setFrontImage] = useState(null);
  const [backImage, setBackImage] = useState(null);
  const [uploadStatus, setUploadStatus] = useState("");
  const [id, setId] = useState(""); // ID field for the API

  const handleFrontImageChange = (e) => {
    setFrontImage(e.target.files[0]);
  };

  const handleBackImageChange = (e) => {
    setBackImage(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!id || !frontImage || !backImage) {
      setUploadStatus("Please fill in all fields and select both images.");
      return;
    }

    const formData = new FormData();
    formData.append("id", id);
    formData.append("front_image", frontImage);
    formData.append("back_image", backImage);

    try {
      const response = await axios.post("http://localhost:8000/upload-customer-images", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      setUploadStatus(response.data.message || "Images uploaded successfully!");
    } catch (error) {
      console.error("Error uploading images:", error);
      setUploadStatus("An error occurred while uploading images. Please try again.");
    }
  };

  return (
    <div className="upload-images-page">
      <h1>Upload Customer Images</h1>
      <form onSubmit={handleSubmit} className="upload-form">
        <div className="form-group">
          <label htmlFor="id">Customer ID:</label>
          <input
            type="text"
            id="id"
            value={id}
            onChange={(e) => setId(e.target.value)}
            placeholder="Enter Customer ID"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="front-image">Front Image:</label>
          <input
            type="file"
            id="front-image"
            accept="image/*"
            onChange={handleFrontImageChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="back-image">Back Image:</label>
          <input
            type="file"
            id="back-image"
            accept="image/*"
            onChange={handleBackImageChange}
            required
          />
        </div>
        <button type="submit" className="upload-button">
          Upload Images
        </button>
      </form>
      {uploadStatus && <div className="upload-status">{uploadStatus}</div>}
    </div>
  );
};

export default UploadImagesPage;
