import React, { useState } from "react";
import axios from "axios";
import "../styles/UploadImagesPage.css";

const UploadImagesPage = ({ inspectionData }) => {
  const [frontImage, setFrontImage] = useState(null);
  const [backImage, setBackImage] = useState(null);
  const [notification, setNotification] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!frontImage || !backImage) {
      setNotification("Please upload both front and back images.");
      return;
    }

    const formData = new FormData();
    formData.append("front_image", frontImage);
    formData.append("back_image", backImage);

    // Append boolean data to the FormData
    Object.keys(inspectionData).forEach((key) =>
      formData.append(key, inspectionData[key])
    );

    try {
      const response = await axios.post(
        "http://localhost:8000/upload-customer-images",
        formData,
        {
          headers: { "Content-Type": "multipart/form-data" },
        }
      );

      if (response.status === 200) {
        setNotification("Images uploaded successfully!");
      } else {
        setNotification("Failed to upload images.");
      }
    } catch (error) {
      console.error("Error uploading images:", error.response?.data || error);
      setNotification("An unexpected error occurred.");
    }
  };

  return (
    <div className="upload-images-page">
      <h1>Upload Images</h1>
      <form onSubmit={handleSubmit} className="upload-form">
        <div className="form-group">
          <label htmlFor="front-image">Front Image:</label>
          <input
            type="file"
            id="front-image"
            accept="image/*"
            onChange={(e) => setFrontImage(e.target.files[0])}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="back-image">Back Image:</label>
          <input
            type="file"
            id="back-image"
            accept="image/*"
            onChange={(e) => setBackImage(e.target.files[0])}
            required
          />
        </div>
        <button type="submit" className="upload-button">
          Upload Images
        </button>
      </form>
      {notification && <div className="notification">{notification}</div>}
    </div>
  );
};

export default UploadImagesPage;
