import React, { useState } from "react";
import axios from "axios";
import "../styles/BaseImageUploadPage.css";

const BaseImageUploadPage = () => {
    const [itemNumber, setItemNumber] = useState("");
    const [frontImage, setFrontImage] = useState(null);
    const [backImage, setBackImage] = useState(null);
    const [notification, setNotification] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Validate input
        if (!itemNumber.trim()) {
            setNotification("Item number is required.");
            return;
        }

        if (!frontImage || !backImage) {
            setNotification("Please upload both front and back images.");
            return;
        }

        const formData = new FormData();
        formData.append("front_image", frontImage);
        formData.append("back_image", backImage);

        try {
            // Make the API call with item_number as a query parameter
            const response = await axios.post(
                `http://localhost:8000/upload-base-images/?item_number=${itemNumber.trim()}`,
                formData,
                {
                    headers: { "Content-Type": "multipart/form-data" },
                }
            );

            setNotification(`Success: ${response.data.message}`);
        } catch (error) {
            console.error("Error uploading images:", error.response?.data || error.message);
            setNotification("Failed to upload images. Please try again.");
        }
    };

    return (
        <div className="base-image-upload-page">
            <h1>Upload Base Images</h1>
            <form onSubmit={handleSubmit} className="upload-form">
                <div className="form-group">
                    <label htmlFor="itemNumber">Item Number:</label>
                    <input
                        type="text"
                        id="itemNumber"
                        value={itemNumber}
                        onChange={(e) => setItemNumber(e.target.value)}
                        placeholder="Enter item number"
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="frontImage">Front Image:</label>
                    <input
                        type="file"
                        id="frontImage"
                        accept="image/*"
                        onChange={(e) => setFrontImage(e.target.files[0])}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="backImage">Back Image:</label>
                    <input
                        type="file"
                        id="backImage"
                        accept="image/*"
                        onChange={(e) => setBackImage(e.target.files[0])}
                        required
                    />
                </div>
                <button type="submit" className="button upload-button">
                    Upload Images
                </button>
            </form>
            {notification && <div className="notification">{notification}</div>}
        </div>
    );
};

export default BaseImageUploadPage;
