import React, { useEffect, useState } from "react";
import { useSearchParams } from "react-router-dom";
import axios from "axios";
import "../styles/ComparisonPage.css";

const ComparisonPage = () => {
    const [searchParams] = useSearchParams();
    const customerId = searchParams.get("customerId");
    const [comparisonResult, setComparisonResult] = useState(null);
    const [error, setError] = useState("");

    useEffect(() => {
        const fetchComparisonResult = async () => {
            if (!customerId) {
                setError("Customer ID is missing.");
                return;
            }

            try {
                const response = await axios.post(
                    `http://localhost:8000/compare-images/`,
                    {
                        base_id: 1, // Replace with your actual base_id
                        customer_id: parseInt(customerId),
                    },
                    {
                        headers: {
                            "Content-Type": "application/json",
                        },
                    }
                );
                setComparisonResult(response.data);
            } catch (err) {
                console.error("Error fetching comparison result:", err);
                setError(err.response?.data?.detail || "Failed to fetch comparison results.");
            }
        };

        fetchComparisonResult();
    }, [customerId]);

    return (
        <div className="comparison-page">
            <h1>Inspection Results</h1>
            {error && <p className="error">{error}</p>}
            {comparisonResult ? (
                <div className="cards-container">
                    <div className="card">
                        <h3 className="card-title">
                            Packaging Condition{" "}
                            <span
                                className="info-tooltip"
                                title="SSI - Structural Similarity Index measures perceptual similarity between images."
                            >
                                ℹ️
                            </span>
                        </h3>
                        <div className="card-content">
                            <p>
                                <strong>Seal Integrity (Cosine Similarity):</strong>{" "}
                                {comparisonResult.front_similarity.toFixed(2)}
                            </p>
                            <p>
                                <strong>Surface Condition (SSI):</strong>{" "}
                                {comparisonResult.ssi_front.toFixed(2)}
                            </p>
                        </div>
                    </div>
                    <div className="card">
                        <h3 className="card-title">
                            Product Condition{" "}
                            <span
                                className="info-tooltip"
                                title="Cosine Similarity evaluates similarity by measuring the cosine of the angle between two feature vectors."
                            >
                                ℹ️
                            </span>
                        </h3>
                        <div className="card-content">
                            <p>
                                <strong>Structural Similarity:</strong>{" "}
                                {comparisonResult.back_similarity.toFixed(2)}
                            </p>
                            <p>
                                <strong>Stain Index (SSI):</strong>{" "}
                                {comparisonResult.ssi_back.toFixed(2)}
                            </p>
                        </div>
                    </div>
                    <div className="card summary-card">
                        <h3 className="card-title">Overall Summary</h3>
                        <div className="card-content">
                            <p>
                                <strong>Aggregate Score:</strong>{" "}
                                {comparisonResult.average_ssi.toFixed(2)}
                            </p>
                            <p>
                                <strong>Final Classification:</strong>{" "}
                                {comparisonResult.overall_condition}
                            </p>
                        </div>
                    </div>
                </div>
            ) : (
                !error && <p>Loading results...</p>
            )}
        </div>
    );
};

export default ComparisonPage;
