import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/ManualScanPage.css";

const ManualScanPage = () => {
    const [orderNumber, setOrderNumber] = useState("");
    const [data, setData] = useState(null);
    const [error, setError] = useState("");

    const navigate = useNavigate();

    const fetchItemDetails = async () => {
        try {
            const response = await axios.get(
                `http://localhost:8000/item_order_instance`, // Replace with your API endpoint
                { params: { order_number: orderNumber } }
            );
            if (response.status === 200) {
                setData(response.data);
                setError(""); // Clear error if successful
            }
        } catch (err) {
            console.error("API error:", err);
            setError(err.response?.data?.detail || "Failed to fetch item details.");
        }
    };

    const handleProceed = () => {
        if (data && data.id) {
            navigate(`/upload-images?customerId=${data.id}`); // Pass customer ID in the URL
        } else {
            setError("Customer ID is missing. Please complete the scan first.");
        }
    };

    return (
        <div className="manual-scan-page">
            <h1>Manual Scan</h1>
            <p className="instruction">
                Enter the Serial Number or Return Order Number below to fetch details.
            </p>
            <div className="input-container">
                <div className="input-wrapper">
                    <input
                        type="text"
                        placeholder="Enter Serial or Return Order Number"
                        value={orderNumber}
                        onChange={(e) => setOrderNumber(e.target.value)}
                    />
                    <button onClick={fetchItemDetails} disabled={!orderNumber}>
                        Fetch Details
                    </button>
                </div>
            </div>
            {error && <div className="error-box">{error}</div>}
            {data && (
                <div className="details-container">
                    <h2>Item Details</h2>
                    {/* Add your data rendering logic here */}
                </div>
            )}
            <button onClick={handleProceed} className="proceed-button">
                Proceed â†’
            </button>
        </div>
    );
};

export default ManualScanPage;
