import React, { useState } from "react";
import axios from "axios";

const CustomerItemDataUpload = () => {
    const [file, setFile] = useState(null);
    const [message, setMessage] = useState("");

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const handleUpload = async () => {
        if (!file) {
            setMessage("Please select a file.");
            return;
        }

        const formData = new FormData();
        formData.append("file", file);

        try {
            const response = await axios.post(
                "http://localhost:8000/upload-customer-item-data",
                formData,
                { headers: { "Content-Type": "multipart/form-data" } }
            );
            setMessage(response.data.message);
        } catch (error) {
            setMessage(
                error.response?.data?.detail || "An error occurred while uploading the file."
            );
        }
    };

    return (
        <div>
            <h1>Upload Customer Item Data</h1>
            <input type="file" accept=".csv" onChange={handleFileChange} />
            <button onClick={handleUpload}>Upload</button>
            {message && <p>{message}</p>}
        </div>
    );
};

export default CustomerItemDataUpload;
