import React, { useState } from "react";
import axios from "axios";
import "../styles/UploadImagesPage.css";

const UploadImagesPage = () => {
  const [id, setId] = useState("");
  const [frontImage, setFrontImage] = useState(null);
  const [backImage, setBackImage] = useState(null);
  const [uploadStatus, setUploadStatus] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!id || !frontImage || !backImage) {
      setUploadStatus("Please provide an ID and both images.");
      return;
    }

    const formData = new FormData();
    formData.append("id", parseInt(id, 10));

    // formData.append("front_image", frontImage);
    // formData.append("back_image", backImage);

    try {
      const response = await axios.post("http://localhost:8000/upload-customer-images", formData, {
        headers: {
          "Content-Type": "application/json",
        },
      });
      setUploadStatus(response.data.message || "Images uploaded successfully!");
    } catch (error) {
      console.error("Error uploading images:", error.response?.data || error);

      // Extract error message from backend response
      if (error.response?.data?.detail) {
        if (Array.isArray(error.response.data.detail)) {
          // Concatenate messages if `detail` is an array
          const errorMessages = error.response.data.detail.map((err) => err.msg).join(", ");
          setUploadStatus(errorMessages);
        } else {
          setUploadStatus(error.response.data.detail.toString());
        }
      } else {
        setUploadStatus("An unexpected error occurred while uploading images.");
      }
    }
  };

  return (
    <div className="upload-images-page">
      <h1>Upload Images</h1>
      <form onSubmit={handleSubmit} className="upload-form">
        <div className="form-group">
          <label htmlFor="id">Customer ID:</label>
          <input
            type="text"
            id="id"
            value={id}
            onChange={(e) => setId(e.target.value)}
            placeholder="Enter Customer ID"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="front-image">Front Image:</label>
          <input
            type="file"
            id="front-image"
            accept="image/*"
            onChange={(e) => setFrontImage(e.target.files[0])}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="back-image">Back Image:</label>
          <input
            type="file"
            id="back-image"
            accept="image/*"
            onChange={(e) => setBackImage(e.target.files[0])}
            required
          />
        </div>
        <button type="submit" className="upload-button">
          Upload Images
        </button>
      </form>
      {uploadStatus && <div className="upload-status">{uploadStatus}</div>}
    </div>
  );
};

export default UploadImagesPage;
